mapper.select:
select user.user_no userNo, user_id userId, nick_name nickName, e_mail eMail, user_exp userExp, user_level userLevel, profile_img profileImg, blog_no blogNo, blog_name blogName, blog_birth blogBirth, is_admin isAdmin, user_name userName, phone_num phoneNum from user LEFT OUTER JOIN blog ON user.user_no = blog.user_no where 1=1
and user.user_no = #{userNo}
and user.user_id = #{userId}

select user_no userNo
from user
where user_id = #{userId}

mapper.update:
update user
set user_pw = #{userPw}, user_name = #{userName}, nick_name = #{nickName}
, e_mail = #{eMail}, phone_num = #{phoneNum}
where user_no = #{userNo} AND join_date is not null

update user
set withdrawal_date = now()
where user_no = #{userNo}

update user
set nick_name = #{nickName}
where user_no = #{userNo}

UPDATE message
SET is_read = 1
WHERE message_no = #{messageNo}

UPDATE message
SET send_delete = 1
WHERE message_no = #{messageNo} AND message_from = #{userNo}

UPDATE message
SET receive_delete = 1
WHERE message_no = #{messageNo} AND message_to = #{userNo}

update
	user
set
	profile_img = #{profileImg}
where
	user_no = #{userNo}

mapper.select:
select following as userNo, nick_name as nickName
from follow inner join user on following = user_no
where follower = #{follower}
	and following = #{following}
